import React, { useEffect, useState } from 'react';

function App() {
    const [data, setData] = useState([]);
    const [selectedMovieCompany, setSelectedCardCompany] = useState('CGV');
    const [selectedDate, setSelectedDate] = useState(new Date('2024-05-10'));

    useEffect(() => {
        const fetchData = async () => {
            const formattedDate = selectedDate.toISOString().slice(0, 10);
            const url = `https://raw.githubusercontent.com/kimyih/movie-chart2/main/${selectedMovieCompany}/${selectedMovieCompany}Chart_${formattedDate}.json`;

            try {
                const response = await fetch(url);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setData(data);
            } catch (error) {
                console.error("There was an error fetching the data:", error);
            }
        };

        fetchData();
    }, [selectedMovieCompany, selectedDate]);

    return (
        <div className="App">
            <h1>Data from External JSON File</h1>

            <div>
                <label>
                    Select Card Company:
                    <select value={selectedMovieCompany} onChange={(e) => setSelectedCardCompany(e.target.value)}>
                        <option value="CGV">CGV</option>
                        <option value="CineQ">CineQ</option>
                        <option value="Moviechart">Moviechart</option>
                        <option value="Serieson">Serieson</option>
                        <option value="Serieson">Megabox</option>
                        {/* Add more options as needed */}
                    </select>
                </label>
            </div>

            <div>
                <label>
                    Select Date:
                    <input
                        type="date"
                        value={selectedDate.toISOString().slice(0, 10)}
                        onChange={(e) => setSelectedDate(new Date(e.target.value))}
                    />
                </label>
            </div>

            <ul>
                {data.map((item, index) => (
                    <li key={index}>
                        <img src={item.imageURL} alt={item.title} width="300px" height="500px" />
                        <div>Rank: {item.rank}</div>
                        <div>Date: {item.date}</div>
                        <div>Title: {item.title}</div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
