import React, { useEffect, useState } from 'react';
import cgvImage from '../assets/img/cgv.jpg';
import cineqImage from '../assets/img/cineq.jpg';
import megaboxImage from '../assets/img/megabox.jpg';
import mvcImage from '../assets/img/mvc.jpg';
import seriesonImage from '../assets/img/serieson.jpg';
import bannerImage from '../assets/img/banner.jpg';
import { Link } from 'react-router-dom';


const urlPatterns = {
    CGV: (company, date) => `${company}/${company}Chart_${date}.json`,
    CineQ: (company, date) => `${company}/${company}Chart_${date}.json`,
    Moviechart: (company, date) => `${company}/${company}Chart_${date}.json`,
    Serieson: (company, date) => `${company}/serieson_chart${date}.json`,
    Megabox: (company, date) => `${company}/${company}Chart_${date}.json`
};
function generateUrl(company, date) {
    const formattedDate = date.toISOString().slice(0, 10);
    const pattern = urlPatterns[company];
    const url = `https://raw.githubusercontent.com/kimyih/movie-chart2/main/${pattern(company, formattedDate)}`;
    return url;
}
function App() {
    const [data, setData] = useState([]);
    const [selectedDate, setSelectedDate] = useState(new Date('2024-05-28'));
    useEffect(() => {
        const fetchData = async () => {
            const url = generateUrl(selectedDate);
            try {
                const response = await fetch(url);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setData(data);
            } catch (error) {
                console.error("There was an error fetching the data:", error);
            }
        };
        fetchData();
    }, [selectedDate]);
    return (
        <div className="Main__Movie">
            <h1>Movie Rank</h1>
            <div className='banner'>
                <img src={bannerImage} alt="banner" />
                <img src={bannerImage} alt="banner" />
                <img src={bannerImage} alt="banner" />
            </div>

            <div className='Cinema'>
                <Link to="/megabox"><img src={megaboxImage} alt="megabox" /></Link>
                <Link to="/cineq"><img src={cineqImage} alt="cineq" /></Link>
                <Link to="/cgv"><img src={cgvImage} alt="cgv" /></Link>
                <Link to="/moviechart"><img src={mvcImage} alt="mvc" /></Link>
                <Link to="/serieson"><img src={seriesonImage} alt="serieson" /></Link>


            </div>
            {/* <div>
                <label>
                    날짜:
                    <input
                        type="date"
                        value={selectedDate.toISOString().slice(0, 10)}
                        onChange={(e) => setSelectedDate(new Date(e.target.value))}
                    />
                </label>
            </div> */}
            <ul className='Main__info'>
                {data.map((item, index) => (
                    <li key={index}>
                        <img src={item.imageURL} alt={item.title} style={{ width: "200px", height: "auto" }} />
                        <div>{item.rank}</div>
                        <div>{item.date}</div>
                        <div>{item.title}</div>
                    </li>
                ))}
            </ul>
        </div>
    );
}
export default App;
