import React, { useEffect, useState } from 'react';

const urlPatterns = {
    CGV: (company, date) => `${company}/${company}Chart_${date}.json`,
    CineQ: (company, date) => `${company}/${company}Chart_${date}.json`,
    Moviechart: (company, date) => `${company}/${company}Chart_${date}.json`,
    Serieson: (company, date) => `${company}/serieson_chart${date}.json`,
    Megabox: (company, date) => `${company}/${company}Chart_${date}.json`
};

function generateUrl(company, date) {
    const formattedDate = date.toISOString().slice(0, 10);
    const pattern = urlPatterns[company];
    const url = `https://raw.githubusercontent.com/kimyih/movie-chart2/main/${pattern(company, formattedDate)}`;
    return url;
}

function App() {
    const [data, setData] = useState([]);
    const [selectedMovieCompany, setSelectedMovieCompany] = useState('CGV');
    const [selectedDate, setSelectedDate] = useState(new Date('2024-05-14'));

    useEffect(() => {
        const fetchData = async () => {
            const url = generateUrl(selectedMovieCompany, selectedDate);

            try {
                const response = await fetch(url);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setData(data);
            } catch (error) {
                console.error("There was an error fetching the data:", error);
            }
        };

        fetchData();
    }, [selectedMovieCompany, selectedDate]);

    return (
        <div className="Main_Movie">
            <h1>Movie RanküèÜ</h1>


            <div className='Cinema'>
                <div onClick={() => setSelectedMovieCompany('CGV')}>CGV</div>
                <div onClick={() => setSelectedMovieCompany('CineQ')}>CineQ</div>
                <div onClick={() => setSelectedMovieCompany('Moviechart')}>Moviechart</div>
                <div onClick={() => setSelectedMovieCompany('Serieson')}>Serieson</div>
                <div onClick={() => setSelectedMovieCompany('Megabox')}>Megabox</div>
            </div>

            <div>
                <label>
                    ÎÇ†Ïßú:
                    <input
                        type="date"
                        value={selectedDate.toISOString().slice(0, 10)}
                        onChange={(e) => setSelectedDate(new Date(e.target.value))}
                    />
                </label>
            </div>

            <ul className='Movie__info'>
                {data.map((item, index) => (
                    <li key={index}>
                        <img src={item.imageURL} alt={item.title} style={{ width: "200px", height: "auto" }} />
                        <div>{item.rank}</div>
                        <div>{item.date}</div>
                        <div>{item.title}</div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
